    'One Punch',
    'Samurai X',
    'Dragon Ball',
  ]);

  const handleAdd = () => {
    categories.push('HunterXHunter');
  }

  return (
    <>
      <h2>GifExpertApp</h2>
      <hr />

      <button>Agregar</button>

      <ol>
        {categories.map(category => {
          return <li key={category}> {category} </li>;
        })}
      </ol>
    </>
  );
};
